from os.path import expanduserimport osimport pandas as pdhome = expanduser("~")path = home+"/Johnathan/Projects/Coffee/coffee"os.chdir(home+"/Johnathan/Projects/Coffee/coffee")df = pd.read_excel(path + "/data/raw/3b - Retail prices.xlsx",                    skiprows = lambda x: x in list(range(3)),                   header = 0)df = (df.rename(columns = {'Calendar years' : 'Country'})         .drop([0,24,31,32,33], axis = 0)     )    country = ["".join([letter for letter in country if not letter.isdigit()])            for country in list(df.Country)]df.Country = country df.Country = df.Country.apply(str.lstrip)df.to_csv(path + "/data/clean/retail_prices.csv")